import React, { Component } from "react";
import { Form, Button, Container } from "react-bootstrap";
const axios = require("axios");

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      first_name: "",
      last_name: "",
      email: "",
      password: "",
    };
    this.onChange = this.onChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  handleSubmit(event) {
    console.log(this.state.first_name);
    console.log(this.state.last_name);
    console.log(this.state.email);
    console.log(this.state.password);
    axios
      .post("", {
        username: this.state.email,
        password: this.state.password,
        first_name: this.state.first_name,
        last_name: this.state.last_name,
      })
      .then(function (res) {
        console.log(res);
        localStorage.setItem("token", res.data.access);
        localStorage.setItem("user", res.config.data);
      })
      .catch(function (err) {
        console.log(err);
      });
    event.preventDefault();
  }
  render() {
    return (
      <Container style={{ marginTop: "100px" }}>
        <Form>
          <Form.Group controlId="formBasicEmail" style={{ width: "300px" }}>
            <Form.Label>First name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter First Name"
              name="first_name"
              value={this.state.first_name}
              onChange={this.onChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail" style={{ width: "300px" }}>
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter Last Name"
              name="last_name"
              value={this.state.last_name}
              onChange={this.onChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail" style={{ width: "300px" }}>
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter username"
              name="email"
              value={this.state.email}
              onChange={this.onChange}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPassword" style={{ width: "300px" }}>
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Password"
              name="password"
              value={this.state.password}
              onChange={this.onChange}
            />
          </Form.Group>
          <Form.Group controlId="select" style={{ width: "300px" }}>
          <select variant="primary">
            <option value=" ">choose the below option</option>
            <option value= "faculty">faculty</option>
            <option value="student">student</option>
          </select>
          </Form.Group>
          <Button variant="success" type="submit" onClick={this.handleSubmit}>
            Register
          </Button>
        </Form>
      </Container>
    );
  }
}
